def convert_string_to_json_cookies(cookies):
	cookies = cookies.replace(" ","")
	list_cookies = cookies.split(";")
	

	json_cookies = dict()

	for cookie in list_cookies:
		if cookie != '':
			cut_cookie = cookie.split("=")
			key = cut_cookie[0]
			value = cut_cookie[1]
			json_cookies[key] = value
	return json_cookies
