def cut_string(key,data,option):
	if option:
		index = data.find(key)
		data = data[index+len(key):]
		return data
	else:
		index = data.find(key)
		return data[:index]
def get_post_id_from_url(url,cookies):
	p = requests.get(url,cookies=cookies)
	data = p.text
	arrKey =["\",\"feedback_id\":\"",
                "\",\"video_id\":\"",
                "\"params\":{\"story_fbid\":\"",
                "\"params\":{\"fbid\":\"",
                "\"story_token\":\"",
                "id=\"actions_"] #['"params":{"story_fbid":"','"params":{"fbid":"','"story_token":"']
	for key in arrKey:
		if data.find(key) != -1:
			result = cut_string(key,data,True)
			result = cut_string('"',result,False)
			return result
	return None

